mapper.select:
  		
  		select * 
		from (select rownum as rn,
		             rno,
		             bno,
		             reply,
		             replyid,
		             replydate,
		             updatedate
		      from(select * 
		           from freereply
		           where bno = #{bno}
		           order by rno desc
		           )
		    )where rn > (#{cri.pageNum}-1) * #{cri.amount} and rn <= #{cri.pageNum} * #{cri.amount}
